import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex);

export default new Vuex.Store({
    state: {
      authToken: null,
    },
    mutations: {
      setAuthToken(state, token) {
        state.authToken = token;
      },
    },
    actions: {
      saveAuthToken({ commit }, token) {
        commit('setAuthToken', token);
      },
    },
    getters: {
      isAuthenticated: (state) => !!state.authToken,
    },
  });
